import { useState, useEffect } from "react";

const description = "Custom hook to retrieve battery information and status.";

interface BatteryManager {
  level: number;
  charging: boolean;
  chargingTime: number;
  dischargingTime: number;
  addEventListener(
    type: string,
    listener: EventListener | EventListenerObject | null,
    options?: boolean | AddEventListenerOptions,
  ): void;
  removeEventListener(
    type: string,
    listener: EventListener | EventListenerObject | null,
    options?: boolean | EventListenerOptions,
  ): void;
}

interface BatteryState {
  supported: boolean;
  loading: boolean;
  level: number | null;
  charging: boolean | null;
  chargingTime: number | null;
  dischargingTime: number | null;
}

interface NavigatorWithBattery extends Navigator {
  getBattery?: () => Promise<BatteryManager>;
}

/**
 * Custom hook to retrieve battery information and status.
 *
 * @description Provides battery information including level, charging status, and estimated time for charging and discharging.
 * Handles unsupported environments by setting `supported` to `false`.
 *
 * @returns {BatteryState} - An object with battery state properties:
 * - `supported` {boolean}: Indicates if the Battery API is supported.
 * - `loading` {boolean}: True while fetching initial battery data.
 * - `level` {number | null}: Battery level as a decimal (0.0 to 1.0), or null if unavailable.
 * - `charging` {boolean | null}: Whether the battery is currently charging, or null if unavailable.
 * - `chargingTime` {number | null}: Estimated time in seconds until fully charged, or null if unavailable.
 * - `dischargingTime` {number | null}: Estimated time in seconds until battery is depleted, or null if unavailable.
 */
export function useBattery() {
  const [batteryState, setBatteryState] = useState<BatteryState>({
    supported: true,
    loading: true,
    level: null,
    charging: null,
    chargingTime: null,
    dischargingTime: null,
  });

  useEffect(() => {
    const _navigator = navigator as NavigatorWithBattery;
    let battery: BatteryManager | null = null;

    /**
     * Updates the battery state when a battery property changes.
     */
    const handleBatteryChange = () => {
      if (battery) {
        setBatteryState({
          supported: true,
          loading: false,
          level: battery.level,
          charging: battery.charging,
          chargingTime: battery.chargingTime,
          dischargingTime: battery.dischargingTime,
        });
      }
    };

    if (!_navigator.getBattery) {
      setBatteryState((prevState) => ({
        ...prevState,
        supported: false,
        loading: false,
      }));
      return;
    }

    _navigator
      .getBattery()
      .then((_battery) => {
        battery = _battery;
        handleBatteryChange();
        battery.addEventListener("levelchange", handleBatteryChange);
        battery.addEventListener("chargingchange", handleBatteryChange);
        battery.addEventListener("chargingtimechange", handleBatteryChange);
        battery.addEventListener("dischargingtimechange", handleBatteryChange);
      })
      .catch(() => {
        setBatteryState((prevState) => ({
          ...prevState,
          supported: false,
          loading: false,
        }));
      });

    return () => {
      if (battery) {
        battery.removeEventListener("levelchange", handleBatteryChange);
        battery.removeEventListener("chargingchange", handleBatteryChange);
        battery.removeEventListener("chargingtimechange", handleBatteryChange);
        battery.removeEventListener(
          "dischargingtimechange",
          handleBatteryChange,
        );
      }
    };
  }, []);

  return batteryState;
}

import { useState, useEffect } from "react";

const description =
  "A hook that provides a function to copy text to the clipboard.";

/**
 * React hook to copy a given text to the user's clipboard.
 *
 * @param {number} [timeout=1500] Time in milliseconds to show the "copied"
 * success state after a successful copy action.
 *
 * @returns {object} An object with two properties:
 * - `isCopied`: A boolean indicating whether the text was successfully copied
 *   to the user's clipboard.
 * - `copy`: A function that takes a string argument and attempts to copy it to
 *   the user's clipboard. If the copy action is successful, it sets the
 *   `isCopied` state to `true`.
 */
export function useClipboard(timeout: number = 1500) {
  const [isCopied, setIsCopied] = useState(false);

  useEffect(() => {
    let timer: ReturnType<typeof setTimeout>;

    if (isCopied) {
      timer = setTimeout(() => {
        setIsCopied(false);
      }, timeout);
    }

    return () => {
      clearTimeout(timer);
    };
  }, [isCopied, timeout]);

  /**
   * Attempts to copy the given text to the user's clipboard.
   *
   * If the copy action is successful, it sets the `isCopied` state to `true`.
   * If the copy action fails, it logs an error to the console and
   * displays a native alert with the message "Failed to copy text".
   *
   * If the browser does not support the Clipboard API, it displays a
   * native alert with the message "Copy not supported".
   *
   * @param {string} text The text to copy to the user's clipboard.
   */
  const copy = (text: string) => {
    if (navigator.clipboard && window.isSecureContext) {
      navigator.clipboard
        .writeText(text)
        .then(() => {
          setIsCopied(true);
        })
        .catch((err) => {
          console.error("Failed to copy: ", err);
          alert("Failed to copy text");
        });
    } else {
      alert("Copy not supported");
    }
  };

  return { isCopied, copy };
}

import { useCallback, useState, useEffect } from "react";

const description = "Custom hook that implements a countdown functionality.";

type CountdownOptions = {
  countStart: number;
  intervalMs?: number;
  countStop?: number;
};

type CountdownControllers = {
  startCountdown: () => void;
  stopCountdown: () => void;
  resetCountdown: () => void;
};

/**
 * Custom hook that implements a countdown functionality.
 *
 * @param {CountdownOptions} options - The options for the countdown.
 * @param {number} options.countStart - The starting count value.
 * @param {number} [options.countStop=0] - The count value at which the countdown stops.
 * @param {number} [options.intervalMs=1000] - The interval in milliseconds at which the countdown updates.
 * @returns {[number, CountdownControllers]} - A tuple containing the current count and countdown controllers.
 */
export function useCountdown({
  countStart,
  countStop = 0,
  intervalMs = 1000,
}: CountdownOptions): [number, CountdownControllers] {
  const [count, setCount] = useState(countStart);
  const [isRunning, setIsRunning] = useState(false);
  const [timer, setTimer] = useState<ReturnType<typeof setInterval> | null>(
    null,
  );

  const startCountdown = useCallback(() => {
    if (!isRunning) {
      setIsRunning(true);
    }
  }, [isRunning]);

  const stopCountdown = useCallback(() => {
    if (timer) {
      clearInterval(timer);
      setTimer(null);
    }
    setIsRunning(false);
  }, [timer]);

  const resetCountdown = useCallback(() => {
    stopCountdown();
    setCount(countStart);
  }, [stopCountdown, countStart]);

  useEffect(() => {
    if (isRunning) {
      setTimer(
        setInterval(() => {
          setCount((prevCount) => {
            if (prevCount <= countStop) {
              stopCountdown();
              return countStop;
            }
            return prevCount - 1;
          });
        }, intervalMs),
      );
    }

    return () => {
      if (timer) {
        clearInterval(timer);
      }
    };
  }, [isRunning, intervalMs, countStop, stopCountdown]);

  return [count, { startCountdown, stopCountdown, resetCountdown }];
}

import { useCallback, useState, useEffect } from "react";

const description = "Custom hook that implements a count-up functionality.";

type CountupOptions = {
  countStart: number;
  intervalMs?: number;
  countStop?: number;
};

type CountupControllers = {
  startCountup: () => void;
  stopCountup: () => void;
  resetCountup: () => void;
};

/**
 * Custom hook that implements a count-up functionality.
 *
 * @param {CountupOptions} options - The options for the count-up.
 * @param {number} options.countStart - The starting count value.
 * @param {number} [options.countStop=Infinity] - The count value at which the count-up stops.
 * @param {number} [options.intervalMs=1000] - The interval in milliseconds at which the count-up updates.
 * @returns {[number, CountupControllers]} - A tuple containing the current count and count-up controllers.
 */
export function useCountup({
  countStart,
  countStop = Infinity,
  intervalMs = 1000,
}: CountupOptions): [number, CountupControllers] {
  const [count, setCount] = useState(countStart);
  const [isRunning, setIsRunning] = useState(false);
  const [timer, setTimer] = useState<ReturnType<typeof setInterval> | null>(
    null,
  );

  const startCountup = useCallback(() => {
    if (!isRunning) {
      setIsRunning(true);
    }
  }, [isRunning]);

  const stopCountup = useCallback(() => {
    if (timer) {
      clearInterval(timer);
      setTimer(null);
    }
    setIsRunning(false);
  }, [timer]);

  const resetCountup = useCallback(() => {
    stopCountup();
    setCount(countStart);
  }, [stopCountup, countStart]);

  useEffect(() => {
    if (isRunning) {
      setTimer(
        setInterval(() => {
          setCount((prevCount) => {
            if (prevCount >= countStop) {
              stopCountup();
              return countStop;
            }
            return prevCount + 1;
          });
        }, intervalMs),
      );
    }

    return () => {
      if (timer) {
        clearInterval(timer);
      }
    };
  }, [isRunning, intervalMs, countStop, stopCountup]);

  return [count, { startCountup, stopCountup, resetCountup }];
}

import {
  useCallback,
  useState,
  type SetStateAction,
  type Dispatch,
} from "react";

type CounterReturnType = {
  increment: () => void;
  decrement: () => void;
  reset: () => void;
  count: number;
  setCount: Dispatch<SetStateAction<number>>;
};

const description =
  "Hook that returns a tuple containing increment, decrement, reset functions, and the current value of the counter.";

/**
 * Hook that returns a tuple containing the following:
 * - `increment`: A function that increments the counter by 1.
 * - `decrement`: A function that decrements the counter by 1.
 * - `reset`: A function that sets the counter to 0.
 * - `count`: The current value of the counter.
 * - `setCount`: A state setter function that sets the counter to the given
 *   value.
 *
 * The hook takes an optional `initialValue` parameter which sets the initial
 * value of the counter. If not provided, the counter is initialized to 0.
 *
 * @param {number} [initialValue] Optional initial value of the counter.
 * @returns {CounterReturnType}
 */
export function useCounter(initialValue?: number): CounterReturnType {
  const [count, setCount] = useState(initialValue ?? 0);

  const increment = useCallback(() => setCount((x) => x + 1), []);
  const decrement = useCallback(() => setCount((x) => x - 1), []);
  const reset = useCallback(() => {
    setCount(initialValue ?? 0);
  }, [initialValue]);

  return {
    increment,
    decrement,
    reset,
    count,
    setCount,
  };
}

import { useCallback, useEffect, useRef } from "react";

const description =
  "Custom hook that debounces a callback function, executing it after the specified delay.";

/**
 * Custom hook that debounces a callback function, executing it after the specified delay.
 *
 * @param {Function} callback - The callback function to debounce.
 * @param {number} delay - The debounce delay in milliseconds.
 * @returns {Function} - A debounced version of the callback function.
 *
 * @example
 * const debouncedSave = useDebounceCallback((value) => {
 *   console.log("Saving data:", value);
 * }, 500);
 */
export function useDebounceCallback<T extends (...args: any[]) => void>(
  callback: T,
  delay: number,
): (...args: Parameters<T>) => void {
  const timer = useRef<ReturnType<typeof setTimeout> | null>(null);

  const debouncedCallback = useCallback(
    (...args: Parameters<T>) => {
      if (timer.current) {
        clearTimeout(timer.current);
      }

      timer.current = setTimeout(() => {
        callback(...args);
      }, delay);
    },
    [callback, delay],
  );

  useEffect(() => {
    return () => {
      if (timer.current) {
        clearTimeout(timer.current);
      }
    };
  }, []);

  return debouncedCallback;
}

import { useEffect, useState } from "react";

const description =
  "Custom hook that debounces a value, returning the value after the specified delay.";

/**
 * Custom hook that debounces a value, returning the value after the specified delay.
 * Useful for cases where you want to prevent frequent updates (e.g., input fields).
 *
 * @template T - The type of the value being debounced.
 * @param {T} value - The value to debounce.
 * @param {number} delay - The debounce delay in milliseconds.
 * @returns {T} - The debounced value.
 *
 * @example
 * const debouncedSearchTerm = useDebounceValue(searchTerm, 500);
 * // The debouncedSearchTerm will update 500ms after the last change in searchTerm.
 */
export function useDebounceValue<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

import { useEffect, useState } from "react";

export interface DeviceDetect {
  isMobile: boolean;
  isDesktop: boolean;
  isTablet: boolean;
  DesktopView: () => boolean;
  MobileView: () => boolean;
  TabletView: () => boolean;
}

const description =
  "A hook that detects the device type and returns a boolean value for each device type.";

/**
 * A hook that detects the device type and returns a boolean value for each device type.
 *
 * @returns {DeviceDetect} - An object containing boolean values for each device type.
 */
export function useDevice(): DeviceDetect {
  const [deviceType, setDeviceType] = useState<string | null>(null);

  useEffect(() => {
    const userAgent = navigator.userAgent;

    if (/tablet/i.test(userAgent)) {
      setDeviceType("tablet");
    } else if (/mobile/i.test(userAgent)) {
      setDeviceType("mobile");
    } else {
      setDeviceType("desktop");
    }
  }, []);

  const isMobile = deviceType === "mobile";
  const isDesktop = deviceType === "desktop";
  const isTablet = deviceType === "tablet";

  const DesktopView = () => isDesktop;
  const MobileView = () => isMobile;
  const TabletView = () => isTablet;

  return {
    isMobile,
    isDesktop,
    isTablet,
    DesktopView,
    MobileView,
    TabletView,
  };
}

import { useRef, useEffect, useCallback } from "react";

const description =
  "A hook that returns a memoized version of a callback that is stable between renders.";

/**
 * A hook that returns a memoized version of a callback that is stable between renders.
 *
 * @param fn The callback function that depends on external values.
 * @returns A stable version of the provided callback.
 */
export function useEventCallback<T extends (...args: any[]) => any>(fn: T): T {
  const ref = useRef<T>(fn);

  useEffect(() => {
    ref.current = fn;
  }, [fn]);

  return useCallback(((...args) => ref.current(...args)) as T, []);
}

import { useEffect, useRef, type RefObject } from "react";

const description =
  "A custom hook to add and clean up an event listener on any target element.";

/**
 * A custom hook to add and clean up an event listener on any target element.
 *
 * @param eventName The event type (e.g. 'click', 'scroll', 'resize', etc.).
 * @param handler The event handler function.
 * @param element Optional: The target element to attach the event listener to (defaults to window).
 */
export function useEventListener<K extends keyof WindowEventMap>(
  eventName: K,
  handler: (event: WindowEventMap[K]) => void,
  element?: RefObject<HTMLElement> | Window,
): void {
  const savedHandler = useRef<(event: WindowEventMap[K]) => void>(handler);

  // Update the ref.current value if the handler changes
  useEffect(() => {
    savedHandler.current = handler;
  }, [handler]);

  useEffect(() => {
    const targetElement: HTMLElement | Window =
      element && "current" in element ? element.current! : window;

    if (!targetElement) return;

    // Type assertion is added here to ensure TypeScript understands the event type correctly
    const eventListener: EventListener = (event) =>
      savedHandler.current(event as WindowEventMap[K]);

    targetElement.addEventListener(eventName, eventListener);

    return () => {
      targetElement.removeEventListener(eventName, eventListener);
    };
  }, [eventName, element]);
}

import { useState, useEffect, useCallback } from "react";

interface UseFetchResult<T, E> {
  data: T | null;
  error: E | null;
  isPending: boolean;
  isSuccess: boolean;
  isError: boolean;
  refetch: () => void;
}

const description = "Custom hook to fetch data from an API endpoint.";

/**
 * Custom hook to fetch data from an API endpoint.
 *
 * @template T - Type of the data returned by the API.
 * @template E - Type of the error returned by the API (default is `string`).
 *
 * @param {string} url - The URL of the API endpoint.
 * @param {RequestInit} [reqOpt] - Optional configuration for the fetch request (e.g., method, headers).
 *
 * @returns {UseFetchResult<T, E>} An object containing the following properties:
 * - `data`: The data returned by the API, or `null` if no data has been received yet.
 * - `error`: The error returned by the API, or `null` if no error has occurred.
 * - `isPending`: A boolean indicating whether the fetch request is currently in progress.
 * - `isSuccess`: A boolean indicating if the fetch request was successful.
 * - `isError`: A boolean indicating if the fetch request resulted in an error.
 * - `refetch`: A function to manually trigger the fetch request again.
 */
export function useFetch<T, E = string>(
  url: string,
  reqOpt?: RequestInit,
): UseFetchResult<T, E> {
  const [data, setData] = useState<T | null>(null);
  const [error, setError] = useState<E | null>(null);
  const [isPending, setisPending] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);

  /**
   * Fetches data from the provided URL using the specified request options.
   *
   * This function uses `fetch` with an `AbortController` to allow for request cancellation if needed.
   * Updates the state with the response data, error, or loading status based on the fetch result.
   */
  const fetchData = useCallback(async () => {
    setisPending(true);
    setIsSuccess(false);

    const controller = new AbortController();
    const signal = controller.signal;

    try {
      const res = await fetch(url, { ...reqOpt, signal });
      const result = await res.json();

      if (res.ok) {
        setData(result);
        setIsSuccess(true);
        setError(null);
      } else {
        setError(result as E);
      }
    } catch (e) {
      if (e instanceof Error && e.name !== "AbortError") {
        setError(e as unknown as E);
      }
    } finally {
      setisPending(false);
    }

    return () => controller.abort();
  }, [url, reqOpt]);

  useEffect(() => {
    fetchData();
  }, []);

  return {
    data,
    error,
    isPending,
    isSuccess,
    isError: !isSuccess && !isPending,
    refetch: fetchData,
  };
}

import { useEffect, useRef, useState, type RefObject } from "react";

type UseFocusCallback = (isFocused: boolean) => void;

const description = "A custom hook to track whether an element is focused.";

/**
 * Returns a ref and boolean indicating whether the element is currently focused.
 *
 * Optionally takes a callback function which is called whenever the element
 * gains or loses focus. The callback is called with a boolean indicating
 * whether the element is currently focused.
 *
 * @param callback Optional callback to run in focus and blur HTML element mode
 * @returns A tuple containing the ref and a boolean indicating whether the
 * element is currently focused.
 */
export function useFocus<T extends HTMLElement>(
  callback?: UseFocusCallback,
): [RefObject<T>, boolean] {
  const [isFocused, setIsFocused] = useState(false);
  const ref = useRef<T>(null);

  useEffect(() => {
    /**
     * A function to run whenever the element gains focus.
     * @param {boolean} isFocused - Whether the element is currently focused.
     * @param {UseFocusCallback} [callback] - The callback function provided to useFocus.
     */
    const handleFocus = () => {
      setIsFocused(true);
      callback?.(true);
    };
    /**
     * A function to run whenever the element loses focus.
     * @param {boolean} isFocused - Whether the element is currently focused.
     * @param {UseFocusCallback} [callback] - The callback function provided to useFocus.
     */
    const handleBlur = () => {
      setIsFocused(false);
      callback?.(false);
    };

    const node = ref.current;
    if (node) {
      node.addEventListener("focus", handleFocus);
      node.addEventListener("blur", handleBlur);
    }

    return () => {
      if (node) {
        node.removeEventListener("focus", handleFocus);
        node.removeEventListener("blur", handleBlur);
      }
    };
  }, [callback]);

  return [ref, isFocused];
}

import { useState, useRef, useEffect } from "react";

const description = "A custom hook to track whether an element is hovered.";

/**
 * Adds an event listener to a given target.
 *
 * @param eventType - The type of event to listen for.
 * @param listener - The event handler function.
 * @param target - The target to which the event listener will be attached.
 */
function useEventListener<K extends keyof HTMLElementEventMap>(
  eventType: K,
  listener: (this: HTMLElement, ev: HTMLElementEventMap[K]) => any,
  target: React.RefObject<HTMLElement>,
): void {
  useEffect(() => {
    const element = target.current;
    if (!element) return;

    element.addEventListener(eventType, listener);
    return () => element.removeEventListener(eventType, listener);
  }, [eventType, listener, target]);
}

/**
 * A custom hook to track whether an element is hovered.
 *
 * @returns A tuple containing a ref to attach to the element and a boolean indicating if it's hovered.
 */
export function useHover<T extends HTMLElement>(): [
  React.RefObject<T>,
  boolean,
] {
  const [isHovered, setIsHovered] = useState(false);
  const ref = useRef<T>(null);

  const handleMouseEnter = () => setIsHovered(true);
  const handleMouseLeave = () => setIsHovered(false);

  useEventListener("mouseenter", handleMouseEnter, ref);
  useEventListener("mouseleave", handleMouseLeave, ref);

  return [ref, isHovered];
}

import { useEffect, useState } from "react";

const description =
  "Custom hook that determines if the component is being rendered on the client side.";

/**
 * A custom React hook that determines if the component is being rendered on the client side.
 *
 * @returns {boolean} - Returns true if the component is mounted on the client, false otherwise.
 */
export function useIsClient(): boolean {
  const [isClient, setClient] = useState<boolean>(false);

  useEffect(() => {
    setClient(true);
  }, []);

  return isClient;
}

import { useEffect, useRef } from "react";

const description =
  "Custom React hook that returns a function to check if the component is currently mounted.";

type UseIsMountedReturnType = () => boolean;

/**
 * A custom React hook that returns a function to check if the component is currently mounted.
 *
 * This hook can be useful for preventing state updates on unmounted components,
 * which can lead to memory leaks and errors in your application.
 *
 * @returns {Function} A function that returns a boolean indicating whether the component is mounted.
 */
export function useIsMounted(): UseIsMountedReturnType {
  const isMounted = useRef<boolean>(false);

  useEffect(() => {
    isMounted.current = true;
    return () => {
      isMounted.current = false;
    };
  }, []);

  return () => isMounted.current;
}

import { useState, useEffect } from "react";

interface KeyConfig {
  key: string;
  ctrl?: boolean;
  alt?: boolean;
  shift?: boolean;
  meta?: boolean;
}

const description = "Custom hook to detect if a specified key is pressed.";

/**
 * Key configuration object for the useKeyPress hook.
 *
 * @typedef {Object} KeyConfig
 * @property {string} key - The key to listen for.
 * @property {boolean} [ctrl] - Whether the Ctrl key must be pressed. Optional.
 * @property {boolean} [alt] - Whether the Alt key must be pressed. Optional.
 * @property {boolean} [shift] - Whether the Shift key must be pressed. Optional.
 */

/**
 * Custom hook to detect if a specified key is pressed.
 *
 * @param {KeyConfig} config - The configuration for the key press detection.
 * @returns {boolean} - A boolean value indicating whether the specified key combination is currently pressed.
 *
 * @example
 * // Usage:
 * const isEnterPressed = useKeyPress({ key: "Enter" });
 * const isCtrlSPressed = useKeyPress({ key: "s", ctrl: true });
 */

export function useKeyPress(config: KeyConfig): boolean {
  const [keyPressed, setKeyPressed] = useState(false);
  const { key: targetKey, ctrl, alt, shift, meta } = config;

  const handleKeyDown = (e: KeyboardEvent) => {
    const { key, ctrlKey, altKey, shiftKey, metaKey } = e;

    if (
      (!ctrl && !alt && !shift && key === targetKey) ||
      (ctrl && key === targetKey && ctrlKey === ctrl) ||
      (alt && key === targetKey && altKey === alt) ||
      (shift && key === targetKey && shiftKey === shift) ||
      (meta && key === targetKey && metaKey === meta)
    ) {
      setKeyPressed(true);
    }
  };

  const handleKeyUp = (e: KeyboardEvent) => {
    const { key, ctrlKey, altKey, shiftKey, metaKey } = e;

    if (
      (!ctrl && !alt && !shift && key === targetKey) ||
      (ctrl && key === targetKey && ctrlKey === ctrl) ||
      (alt && key === targetKey && altKey === alt) ||
      (shift && key === targetKey && shiftKey === shift) ||
      (meta && key === targetKey && metaKey === meta)
    ) {
      setKeyPressed(false);
    }
  };

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  return keyPressed;
}

import { useSyncExternalStore } from "react";

const description =
  "Custom hook that returns the current language of the user's browser, based on their browser settings.";

/**
 * Subscribes to the `languagechange` event on the `window` object to detect changes in the user's preferred language.
 * This function returns an unsubscribe function to remove the event listener.
 *
 * @param {() => void} callback - The callback function that triggers when the language changes.
 * @returns {() => void} A function to unsubscribe from the `languagechange` event.
 */
function langSubscribe(callback: () => void) {
  window.addEventListener("languagechange", callback);
  return () => window.removeEventListener("languagechange", callback);
}

/**
 * Retrieves the current language of the user's browser.
 *
 * @returns {string} The current language code (e.g., 'en-US', 'fr', etc.).
 */
const getLang = (): string => navigator.language;

/**
 * Custom hook that returns the current language of the user's browser, automatically updating when the language changes.
 *
 * @returns {string} The current browser language.
 */
export function useLang(): string {
  return useSyncExternalStore(langSubscribe, getLang);
}

import { useState, useEffect } from "react";

const description =
  "Custom hook that manages a state variable with local storage.";

/**
 * Custom hook that manages a state variable with local storage.
 *
 * @param {string} key - The key for the local storage item.
 * @param {T} initialValue - The initial value for the state variable.
 * @returns {[T, (value: T) => void]} An array containing the state variable and a setter function.
 *
 * @example
 * const [name, setName] = useLocalStorage('name', 'John Doe');
 * // The name variable will be persisted in local storage under the key 'name'.
 */
export function useLocalStorage<T>(
  key: string,
  initialValue: T,
): [T, (value: T) => void] {
  const [storedValue, setStoredValue] = useState<T>(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(`Error reading local storage key "${key}":`, error);
      return initialValue;
    }
  });

  const setValue = (value: T) => {
    try {
      const valueToStore =
        value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      console.error(`Error setting local storage key "${key}":`, error);
    }
  };

  useEffect(() => {
    const handleStorageChange = (event: StorageEvent) => {
      if (event.key === key) {
        setStoredValue(
          event.newValue ? JSON.parse(event.newValue) : initialValue,
        );
      }
    };

    window.addEventListener("storage", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, [key, initialValue]);

  return [storedValue, setValue];
}

import { useState, useEffect } from "react";

const description =
  "Custom hook that returns the current online/offline status of the browser.";

/**
 * Custom hook that returns the current online/offline status of the browser.
 * Optionally, takes a callback function to be invoked whenever the status changes.
 *
 * @param {function} callback - Optional callback to run on status change.
 * @returns {boolean} - `true` if online, `false` if offline.
 */
export function useOnlineStatus(
  callback?: (isOnline: boolean) => void,
): boolean {
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      if (callback) callback(true);
    };

    const handleOffline = () => {
      setIsOnline(false);
      if (callback) callback(false);
    };

    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
    };
  }, [callback]);

  return isOnline;
}

import { useState, useLayoutEffect } from "react";

const description =
  "Custom hook that tracks and returns the current scroll position, and provides a method to scroll to specific coordinates.";

/**
 * Represents the scroll position in the x and y directions.
 */
type ScrollPosition = {
  x: number;
  y: number;
};

/**
 * Custom hook that tracks and returns the current scroll position,
 * and provides a method to scroll to specific coordinates.
 *
 * @returns {{ position: ScrollPosition; scrollTo: (options: ScrollToOptions) => void }}
 */
export function useScroll(): {
  position: ScrollPosition;
  scrollTo: (options: ScrollToOptions) => void;
} {
  const [position, setPosition] = useState<ScrollPosition>({
    x: window.scrollX,
    y: window.scrollY,
  });

  const handleScroll = () => {
    setPosition({
      x: window.scrollX,
      y: window.scrollY,
    });
  };

  useLayoutEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return {
    position,
    scrollTo: window.scrollTo.bind(window),
  };
}

