import { useCallback, useRef, useEffect } from "react";

const description =
  "Custom React hook that returns a throttled version of the provided function.";

/**
 * Interface for throttle options
 * @interface ThrottleOptions
 * @property {number} [wait=300] - The number of milliseconds to throttle invocations to
 * @property {boolean} [leading=true] - Specify invoking on the leading edge of the timeout
 * @property {boolean} [trailing=true] - Specify invoking on the trailing edge of the timeout
 */
interface ThrottleOptions {
  wait?: number;
  leading?: boolean;
  trailing?: boolean;
}

/**
 * Custom React hook that returns a throttled version of the provided function.
 * The throttled function will only execute at most once per every `wait` milliseconds.
 *
 * @template T - Generic type extending function
 * @param {T} fn - The function to throttle
 * @param {ThrottleOptions} [options] - The configuration options
 * @param {number} [options.wait=300] - The number of milliseconds to throttle invocations to
 * @param {boolean} [options.leading=true] - If true, the function will execute on the leading edge of the timeout
 * @param {boolean} [options.trailing=true] - If true, the function will execute on the trailing edge of the timeout
 *
 * @returns {(...args: Parameters<T>) => void} A throttled version of the provided function
 */
export function useThrottle<T extends (...args: any[]) => any>(
  fn: T,
  options: ThrottleOptions = {},
): (...args: Parameters<T>) => void {
  const { wait = 300, leading = true, trailing = true } = options;

  const timeoutRef = useRef<number | null>(null);
  const lastRunRef = useRef<number>(0);
  const lastArgsRef = useRef<Parameters<T> | null>(null);

  useEffect(() => {
    return () => {
      if (timeoutRef.current !== null) {
        window.clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return useCallback(
    (...args: Parameters<T>) => {
      const now = Date.now();
      const elapsed = now - lastRunRef.current;

      lastArgsRef.current = args;

      const execute = () => {
        fn(...(lastArgsRef.current as Parameters<T>));
        lastRunRef.current = Date.now();
      };

      if (timeoutRef.current !== null) {
        window.clearTimeout(timeoutRef.current);
        timeoutRef.current = null;
      }

      if (elapsed > wait) {
        if (leading) {
          execute();
        } else if (trailing) {
          timeoutRef.current = window.setTimeout(execute, wait);
        }
      } else if (trailing) {
        timeoutRef.current = window.setTimeout(execute, wait - elapsed);
      }
    },
    [fn, wait, leading, trailing],
  );
}

import { useState } from "react";

const description = "Custom hook to change the page title.";

/**
 * Custom hook to change the page title.
 *
 * @description Provides a stateful title and a function to update both the state and the document title.
 *
 * @returns {Object} - An object containing:
 * - `title` {string}: The current page title.
 * - `changeTitle` {(newTitle: string) => void}: Function to update the page title.
 */

export function useTitle() {
  const [title, setTitle] = useState<string>(document.title);

  /**
   * Updates the document title and internal title state.
   *
   * @param {string} newTitle - The new title to set for the page.
   */
  const changeTitle = (newTitle: string) => {
    setTitle(newTitle);
    document.title = newTitle;
  };

  return { title, changeTitle };
}

import {
  useState,
  useCallback,
  type Dispatch,
  type SetStateAction,
} from "react";

const description = "Returns a stateful value and two functions to update it.";

/**
 * Returns a stateful value and two functions to update it.
 *
 * @param defaultValue - The initial value of the state. Defaults to `false`.
 *
 * @returns A tuple containing:
 * - `boolean`: The current value of the state.
 * - `() => void`: A function to toggle the state.
 * - `Dispatch<SetStateAction<boolean>>`: A function to set the state to a new value.
 */
export function useToggle(
  defaultValue?: boolean,
): [boolean, () => void, Dispatch<SetStateAction<boolean>>] {
  const [value, setValue] = useState(!!defaultValue);

  const toggle = useCallback(() => {
    setValue((x) => !x);
  }, []);

  return [value, toggle, setValue];
}

import { useEffect, useRef } from "react";

const description =
  "A custom React hook that executes a callback function when the component unmounts.";

/**
 * A custom React hook that executes a callback function when the component unmounts.
 *
 * @param {() => void} fn - The callback function to be executed on unmount.
 */
export function useUnmount(fn: () => void): void {
  const fnRef = useRef<() => void>(fn);

  useEffect(() => {
    fnRef.current = fn;

    return () => {
      fnRef.current();
    };
  }, [fn]);
}

import { useState, useEffect } from "react";

type WindowSize = {
  width: number;
  height: number;
};

const description =
  "Custom hook that tracks and returns the current window size.";

/**
 * Custom hook that tracks and returns the current window size.
 * @returns {WindowSize} An object containing the current width and height of the window.
 */
export function useWindowSize(): WindowSize {
  const [windowSize, setWindowSize] = useState<WindowSize>({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  const handleResize = () => {
    setWindowSize({
      width: window.innerWidth,
      height: window.innerHeight,
    });
  };

  useEffect(() => {
    window.addEventListener("resize", handleResize);

    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return windowSize;
}
